import { createSlice, nanoid, createAsyncThunk } from '@reduxjs/toolkit'
import controller from '../controller'
import web3 from './../web3'





export const getUserAsync = createAsyncThunk('node/getUserAsync', async () => {
  const acts = await web3.eth.getAccounts(); 
  return acts[0];
})


export const getBalanceAsync = createAsyncThunk('node/getBalanceAsync', async () => {
  const node = await getNodeAsync();
  const response = await controller.methods.getCumulativeNodeDeposit(parseInt(node)).call();
  return response;
})


export const stakeRenAsync = createAsyncThunk('node/stakeRenAsync', async (_amount, _nodeId, _sender) => {
  const response = await controller.methods.stakeRen(_amount,_nodeId).send({from: _sender});
  return response;
})


export const createNodeAsync = createAsyncThunk('node/createNodeAsync', async () => {
  const response = await controller.methods.createNode().send();
  return response;
})

export const getNodeAsync = createAsyncThunk('node/getNodeAsync', async () => {
  const response = await controller.nodeCount.call();
  console.log("response is",response);
  return parseInt(parseInt(response) - 1);
})

export const getDepositStatusAsync = createAsyncThunk('node/getDepositStatusAsync',async() => {
  const currentNodeBalance = getBalanceAsync(getNodeAsync());
  if(currentNodeBalance == 50000){
    return 'accepting';
  }
  else{
    return 'full';
  }
})


const nodeSlice = createSlice({
    name: 'node',
    initialState: {
      balance: 0,
      status:'idle',
      error:'',
      currentNode: 0,
      user:'',
      depositStatus:'full'
    },
    reducers: {
      // omit existing reducers here
    },
    extraReducers: (builder) => {
      // Add reducers for additional action types here, and handle loading state as needed
      builder.addCase(getUserAsync.fulfilled, (state, action) => {
        // Add user to the state array
        state.status = 'succeeded'
        state.user = action.payload

      })
      .addCase(getBalanceAsync.fulfilled, (state, action) => {
        // Add user to the state array
        state.status = 'succeeded'
        state.balance = action.payload
      })
      .addCase(getNodeAsync.fulfilled, (state, action) => {
        // Add user to the state array
        state.status = 'succeeded'
        state.currentNode = action.payload
      })
      .addCase(getDepositStatusAsync.fulfilled, (state, action) => {
        // Add user to the state array
        state.status = 'succeeded'
        state.depositStatus = action.payload
      })
    }
})


  export default nodeSlice.reducer
