import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  fetchBalanceAsync,
} from './nodeSlice';
//import styles from './Node.module.css';

export function Node() {
  const balance = useSelector(state => state.node.balance)
  const fetchStatus = useSelector(state => state.node.status)
  const error = useSelector(state => state.node.error)
  const dispatch = useDispatch();


  const [incrementAmount, setIncrementAmount] = useState('50');

  const incrementValue = Number(incrementAmount) || Number(50);
  return (
    <div>
      <div>

        <button onClick={() => dispatch(createNode())}>Create Node</button>
        <h2> </h2>
        <button
          aria-label="Get Node balance"
          onClick={() => dispatch(fetchBalanceAsync())}
        >
          Balance
        </button>
        <span>Balance is : {balance}</span>
        <input
          aria-label="Set increment amount"
          value={incrementAmount}
          onChange={(e) => setIncrementAmount(e.target.value)}
        />
        <button aria-label="STAKE" onClick={() => dispatch(depositRen())}>STAKE</button>

      </div>

    </div>
  );
}
