import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';

import Select from 'react-select';

import {
  getBalanceAsync,
  getUserAsync,
  getNodeAsync,
  getDepositStatusAsync,
  updateNodeStats,
  isOwner
} from './nodeSlice';
//import styles from './Node.module.css';

export function Node() {
  //const fetchStatus = useSelector(state => state.node.status)
  //const error = useSelector(state => state.node.error)
  const user = useSelector(state => state.node.user);
  const currentNode = useSelector(state => state.node.currentNode);
  const balance = useSelector(state => state.node.balance);
  //const [user, setUser] = useState(null);

  const options = [
  { value: 'a', label: 'a' },
  { value: 'b', label: 'b' },
  ];

  const [selectedOption, setSelectedOption] = useState(null);
  const handleChange = (selectedOption) => {
    setSelectedOption(selectedOption);
    document.write(`Option selected:`, selectedOption.value); //this prints the selected option
  }


  const dispatch = useDispatch();

  //dispatch(getUserAsync());
  const status = useSelector(state => state.node.status) 
  const depositStatus = useSelector(state => state.node.depositStatus) 
  const nodeBalance = useSelector(state => state.node.balance)
  const owner = useSelector(state => state.node.owner)

   useEffect(() => {
    if (status == 'idle') {
      dispatch(getUserAsync())
      dispatch(isOwner())
      dispatch(updateNodeStats())

    }
  }, [])



  let content

  if (owner == 'yes') {
    content = <div><button>Create Node</button></div>
  }
  else{
    content = <div>Owner yet to create a Node</div>
  }


  return (
    <section className="posts-list">
      <h2>User: {user}</h2>

      <h2>Node id: {currentNode}</h2>
      <h2>Node Balance: {nodeBalance}</h2>
      <h2>deposit Status : {depositStatus} </h2>
      <h2>owner: {owner}</h2>
      <h2>{content}</h2>
      <Select
        value={selectedOption}
        onChange={this.handleChange}
        options={options}
      />

    </section>
  )
}
