import { createSlice, nanoid, createAsyncThunk } from '@reduxjs/toolkit'
import controller from '../controller'

const initialState = {
  balance: 0,
  status:'idle',
  error:''
}

export const fetchBalanceAsync = createAsyncThunk('node/fetchBalance', async () => {
  const response = await controller.methods.getCumulativeNodeDeposit(0).call();
  return response;
})


const nodeSlice = createSlice({
    name: 'node',
    initialState,
    reducers: {
      // omit existing reducers here
    },
    extraReducers: {
      [fetchBalance.pending]: (state, action) => {
        state.status = 'loading'
      },
      [fetchBalance.fulfilled]: (state, action) => {
        state.status = 'succeeded'
        // Add any fetched posts to the array
        state.balance = action.payload;
      },
      [fetchBalance.rejected]: (state, action) => {
        state.status = 'failed'
        state.error = action.error.message
      }
    }
  })

  //export const { fetchBalance } = nodeSlice.actions;

  export default nodeSlice.reducer
