import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  fetchBalanceAsync,
  getUserAsync,
  selectUser
} from './nodeSlice';
//import styles from './Node.module.css';

export function Node() {
  const balance = useSelector(state => state.node.balance)
  //const fetchStatus = useSelector(state => state.node.status)
  //const error = useSelector(state => state.node.error)
  const curentNode = useSelector(state => state.node.currentNode);


  const user = useSelector(selectUser);
  //const [user, setUser] = useState(null);

  const dispatch = useDispatch();

  //dispatch(getUserAsync());

  const userStatus = useSelector(state => state.node.status) 
  const nodeBalance = useSelector(State => state.node.balance)
  useEffect(() => {
    if (userStatus === 'idle') {
      dispatch(getUserAsync())
    }
  }, [userStatus, dispatch])




  let content

  if (userStatus === 'succeeded') {
    content = <div>done</div>
  }
  else if(userStatus === 'idle'){
    content = <div>gettting</div>
  }

  return (
    <section className="posts-list">
      <h2>User: {user}</h2>
      <h2>Node Balance: {nodeBalance}</h2>

    </section>
  )
}
