import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
  fetchBalanceAsync,
} from './nodeSlice';
//import styles from './Node.module.css';

export function Node() {
  const balance = useSelector(fetchBalanceAsync)
  const fetchStatus = useSelector(state => state.node.status)
  const error = useSelector(state => state.node.error)
  const dispatch = useDispatch();

  return (
    <div>
      <div>
        <button
          aria-label="Get Node balance"
          onClick={() => dispatch(fetchBalanceAsync())}
        >
          Balance
        </button>
        <span>{balance}</span>

      </div>

    </div>
  );
}
